mapper.update:
UPDATE reply
reply_text = #{replyText}, reply_image_name = #{replyImageName}, reply_update_date = current_timestamp
WHERE reply_no = #{replyNo}

mapper.select:
SELECT *
FROM community_logs
WHERE community_logs_no = #{communityLogsNo}

SELECT *
FROM community_logs
WHERE user_id = #{userId} AND logs_type = #{logsType}

SELECT *
FROM report
WHERE user_id = #{userId}

SELECT *
FROM report
WHERE report_no = #{reportNo}

mapper.update:
UPDATE report

	report_status = #{reportStatus}
	report_result = #{reportResult}

WHERE report_no = #{reportNo}
