	) inner_table
LIMIT #{limit} OFFSET #{offset}

mapper.sql:
SELECT record_no, record_user_id, record_title, latitude, longitude, checkpoint_address, DATE_FORMAT(checkpoint_date, '%Y-%m-%d %H:%i:%s') AS checkpoint_date, media_name, category_no, record_text, temp_type, DATE_FORMAT(record_add_date, '%Y-%m-%d %H:%i:%s') AS record_add_date, DATE_FORMAT(shared_date, '%Y-%m-%d %H:%i:%s') AS shared_date, update_count, d_day_date, timecapsule_type FROM record
latitude BETWEEN #{minLatitude} AND #{maxLatitude} AND longitude BETWEEN #{minLongitude} AND #{maxLongitude}
AND record_user_id IN ( SELECT target_id FROM follow_block WHERE user_id=#{userId} AND FB_TYPE=0 ) AND shared_date IS NOT NULL
AND shared_date IS NOT NULL AND NOT record_user_id=#{userId}
AND record_user_id=#{userId}
AND temp_type=1 AND timecapsule_type=0
ORDER BY rand() limit 30

