mapper.select:
SELECT 
	COUNT(*) AS checkpoint_count, 
	(SELECT 
	DATE_FORMAT(checkpoint_date, '%Y-%m-%d %H:%i:%s') AS checkpoint_date 
	FROM record 
	WHERE checkpoint_address=#{checkpointAddress:VARCHAR} 
	AND record_user_id=#{recordUserId:VARCHAR}
	AND timecapsule_type=0 
	ORDER BY checkpoint_date DESC 
	LIMIT 1) AS checkpoint_date 
FROM record 
WHERE checkpoint_address=#{checkpointAddress:VARCHAR}  
AND record_user_id=#{recordUserId:VARCHAR} 
AND timecapsule_type=0 
GROUP BY checkpoint_address=#{checkpointAddress:VARCHAR}

SELECT
inner_table.*
FROM
	(

	) inner_table
LIMIT #{limit} OFFSET #{offset}

SELECT a1.record_no AS record_no, a1.record_user_id AS record_user_id, a4.nickname AS nickname, a4.profile_image_name AS profile_image_name, a1.record_title AS record_title, a1.latitude AS latitude, a1.longitude AS longitude, a1.checkpoint_address AS checkpoint_address, DATE_FORMAT(a1.checkpoint_date, '%Y-%m-%d %H:%i:%s') AS checkpoint_date, a1.media_name AS media_name, a1.category_no AS category_no, a3.category_name AS category_name, a3.category_imoji AS category_imoji, a1.record_text AS record_text, a1.temp_type AS temp_type, DATE_FORMAT(a1.record_add_date, '%Y-%m-%d %H:%i:%s') AS record_add_date, DATE_FORMAT(a1.shared_date, '%Y-%m-%d %H:%i:%s') AS shared_date, a1.update_count AS update_count, a1.d_day_date AS d_day_date, a1.timecapsule_type AS timecapsule_type FROM record a1 LEFT JOIN category a3 ON a1.category_no = a3.category_no LEFT JOIN users a4 ON a1.record_user_id = a4.user_id
a1.latitude BETWEEN #{minLatitude} AND #{maxLatitude} AND a1.longitude BETWEEN #{minLongitude} AND #{maxLongitude} AND a1.temp_type=1 AND a1.timecapsule_type=0
OR( a1.latitude BETWEEN #{minLatitude} AND #{maxLatitude} AND a1.longitude BETWEEN #{minLongitude} AND #{maxLongitude} AND a1.record_user_id IN ( SELECT target_id FROM follow_block WHERE user_id=#{userId} AND FB_TYPE=0 ) AND a1.shared_date IS NOT NULL AND a1.temp_type=1 AND a1.timecapsule_type=0
AND (a1.record_text LIKE #{searchKeyword} OR a1.record_title LIKE #{searchKeyword} OR a1.record_no IN ( SELECT record_no FROM image_tag WHERE IMAGE_TAG_TYPE = 0 AND image_tag_text LIKE #{searchKeyword}))
AND a1.category_no=#{categoryNo}
)
OR ( a1.latitude BETWEEN #{minLatitude} AND #{maxLatitude} AND a1.longitude BETWEEN #{minLongitude} AND #{maxLongitude} AND a1.shared_date IS NOT NULL AND NOT a1.record_user_id=#{userId} AND a1.temp_type=1 AND a1.timecapsule_type=0
AND (a1.record_text LIKE #{searchKeyword} OR a1.record_title LIKE #{searchKeyword} OR a1.record_no IN ( SELECT record_no FROM image_tag WHERE IMAGE_TAG_TYPE = 0 AND image_tag_text LIKE #{searchKeyword}))
AND a1.category_no=#{categoryNo}
)
OR ( a1.latitude BETWEEN #{minLatitude} AND #{maxLatitude} AND a1.longitude BETWEEN #{minLongitude} AND #{maxLongitude} AND a1.record_user_id=#{userId} AND a1.temp_type=1 AND a1.timecapsule_type=0
AND (a1.record_text LIKE #{searchKeyword} OR a1.record_title LIKE #{searchKeyword} OR a1.record_no IN ( SELECT record_no FROM image_tag WHERE IMAGE_TAG_TYPE = 0 AND image_tag_text LIKE #{searchKeyword}))
AND a1.category_no=#{categoryNo}
)
ORDER BY rand() LIMIT #{limit}
